  unsigned char encrypted_char;

  for (int i = 0; i < strlen(password); i++)
  {
    encrypted_char = password[i] ^ key[i % sizeof(key)];

    if (encrypted_char && !(encrypted_char & 0x2000))
      printf("%c", encrypted_char);
  }

  printf("\n");

  return (0);
}

